export default {
	items: Get_Units.data,
	selectedItem : {pp_gla : 122},
	getItems: async () => {
		//use async-await or promises
		if(!Get_Units.data) {
			await Get_Units.run();
		}
		this.items = Get_Units.data;
		return this.items;
	},
	options : () => {
		let options = [];
		if(this.items) {
			this.items.forEach(function(item){
				options.push({
					label : item.pp_name,
					value : item.pp_unitid
				});
			});
		}
		return options;
	},
	getItem: (id) => {
		return this.items.find((item) => {return item.pp_unitid === id}) || {};
	},
	onSelectUnit : () => {
		this.selectedItem = this.getItem(select_unit_no.selectedOptionValue);
		appsmith.store.pp_gla = this.selectedItem.pp_gla;
		console.log(appsmith.store.pp_gla);
	},
	getGla:(id) => {
		if(id) {
			this.selectedItem = this.getItem(select_unit_no.selectedOptionValue);
		}
	}
}
