export default {
	items: Get_Units.data,
	selectedItem : {},
	getItems: async () => {
		//use async-await or promises
		if(!Get_Units.data) {
			await Get_Units.run();
		}
		this.items = Get_Units.data;
		return this.items;
	},
	options : () => {
		let options = [];
		if(this.items) {
			this.items.forEach(function(item){
				options.push({
					label : item.pp_name,
					value : item.pp_unitid
				});
			});
		}
		return options;
	},
	getItem: (id) => {
		return this.items.find((item) => {return item.pp_unitid === id}) || {};
	},
	onSelectUnit : () => {
		this.selectedItem = this.getItem(select_unit_no.selectedOptionValue);
		storeValue('unit' , this.selectedItem);
		appsmith.store.unit['pp_gla@OData.Community.Display.V1.FormattedValue'];
		input_unit_gla.value = this.selectedItem? this.selectedItem['pp_gla@OData.Community.Display.V1.FormattedValue'] : "";
		input_unit_gla.inputValue = this.selectedItem? this.selectedItem['pp_gla@OData.Community.Display.V1.FormattedValue'] : "";
	}
}
