export default {
	items: Get_Units.data,
	selectedItem : {},
	getItems: async () => {
		//use async-await or promises
		if(!Get_Units.data) {
			await Get_Units.run();
		}
		this.items = Get_Units.data;
		return this.items;
	},
	options : () => {
		let options = [];
		if(this.items) {
			this.items.forEach(function(item){
				options.push({
					label : item.pp_name,
					value : item.pp_unitid
				});
			});
		}
		return options;
	},
	getItem: (id) => {
		if(id && !this.selectedItem[id]) {
			this.selectedItem[id] =  this.items.find((item) => {return item.pp_unitid === id}) || {};
		}
		return id ? this.selectedItem[id] : {};
	},
	getGLA:(id) => {
		return this.getItem(id)["pp_gla@OData.Community.Display.V1.FormattedValue"] || "";
	},
	getUnitType:(id) => {
		return this.getItem(id)["pp_unittype@OData.Community.Display.V1.FormattedValue"] || "";
	},
	getLevel:(id) => {
		return this.getItem(id)["_pp_level_value@OData.Community.Display.V1.FormattedValue"] || "";
	},
}
